#!/bin/bash# 임계값 설정CPU_THRESHOLD=95MEMORY_THRESHOLD=80# CPU 사용량 가져오기 (IDLE 값을 제외한 사용량)CPU_USAGE=$(top -l 1 | awk '/CPU usage:/ {print 100 - $8}')# 메모리 사용량 가져오기MEMORY_USAGE=$(vm_stat | grep 'Pages active' | awk '{print $3}' | sed 's/\.//')TOTAL_MEMORY=$(vm_stat | grep 'Pages free' | awk '{print $3}' | sed 's/\.//')PERCENT_MEMORY=$(( ($MEMORY_USAGE * 100) / $TOTAL_MEMORY ))# 임계값 초과 확인if (( $(echo "$CPU_USAGE > $CPU_THRESHOLD" |bc -l) )); then    osascript -e 'display notification "High CPU Usage!" with title "System Alert"'fiif (( $(echo "$PERCENT_MEMORY > $MEMORY_THRESHOLD" |bc -l) )); then    osascript -e 'display notification "High Memory Usage!" with title "System Alert"'fi