#!/bin/bash# 설정 변수CURRENT_PUBLIC_IP="your_current_ip_here"TO="kkwjdfo@gmail.com"# 현재 외부 IP 조회IP=$(curl -s ifconfig.me)# IP 변경 감지if [ "$IP" != "$CURRENT_PUBLIC_IP" ]; then    # 이메일 내용 설정    SUBJECT="Monitoring Alert: Public IP Address Changed"    MESSAGE="The public IP address has changed from $CURRENT_PUBLIC_IP to $IP on $(hostname) as of $(date)."    # sendmail을 사용하여 이메일 전송    echo -e "Subject: $SUBJECT\nTo: $TO\n\n$MESSAGE" | sendmail -tfi